import classes from './Checkout.module.css';
import { useRef, useState } from 'react';
const Checkout = (props) => {
    // for true value
    const [formInputsValidity,isValid]=useState({
        name:true,
        street:true,
        postal:true,
        city:true
    })
    const isEmpty=(value)=>value.trim().length===''
    const isFive=(value)=>value.trim().length===5
    const nameInput=useRef()
    const streetInput=useRef()
    const postalInput=useRef()
    const cityInput=useRef()
    const onSubmitHandler=(event)=>{
        // jo value store hogi after form submission
    const name=nameInput.current.value
    const street=streetInput.current.value
    const postal=postalInput.current.value
    const city=cityInput.current.value
        event.preventDefault()
        const isName=!isEmpty(name)
        const isStreet=!isEmpty(name)
        const isPostal=!isEmpty(name)
        const isCity=!isFive(name)
        isValid({
            name:isName,
            street:isStreet,
            postal:isPostal,
            city:isCity
        })
        const formValid=
            name && street && postal&& city
        if(!formValid)
        {
            return
        }

    }
    const nameControlClasses = `${classes.control} ${
        formInputsValidity.name ? '' : classes.invalid
      }`;
      const streetControlClasses = `${classes.control} ${
        formInputsValidity.street ? '' : classes.invalid
      }`;
      const postalCodeControlClasses = `${classes.control} ${
        formInputsValidity.postalCode ? '' : classes.invalid
      }`;
      const cityControlClasses = `${classes.control} ${
        formInputsValidity.city ? '' : classes.invalid
      }`;
  return (
    <form className={classes.form} onSubmit={onSubmitHandler}>
      <div className={nameControlClasses}>
        <label htmlFor='name'>Your Name</label>
        <input type='text' id='name' ref={nameInput}/>
        {!formInputsValidity && <p>enter correct name</p>}
      </div>
      <div className={streetControlClasses}>
        <label htmlFor='street'>Street</label>
        <input type='text' id='street' ref={streetInput}/>
        {!formInputsValidity && <p>enter correct name</p>}
        </div>
      <div className={postalCodeControlClasses}>
        <label htmlFor='postal'>Postal Code</label>
        <input type='text' id='postal' ref={postalInput}/>
        {!formInputsValidity && <p>enter correct name</p>}
        </div>
      <div className={cityControlClasses}>
        <label htmlFor='city'>City</label>
        <input type='text' id='city'ref={cityInput} />
        {!formInputsValidity && <p>enter correct name</p>}

      </div>
      <div className={classes.actions}>
        <button type='button' onClick={props.onCancel}>
          Cancel
        </button>
        <button className={classes.submit}>Confirm</button>
      </div>
    </form>
  );
};

export default Checkout;
